    private void cmsSalirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cmsSalirActionPerformed
        this.hide();
        j.show();

    }//GEN-LAST:event_cmsSalirActionPerformed

    private void cmdSiguienteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cmdSiguienteActionPerformed
        if (this.opc < 4) {
            this.opc = this.opc + 1;
            try {
                new AlbumController().cargarfiguritas(this.getPaises()[this.opc - 1].getNombre_pais(), this);
            } catch (ClassNotFoundException | SQLException ex) {
                Logger.getLogger(VALBUM.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
    }//GEN-LAST:event_cmdSiguienteActionPerformed

    private void cmdAnteriorActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cmdAnteriorActionPerformed


        if (this.opc > 1) {
            this.opc = this.opc - 1;
            try {
                new AlbumController().cargarfiguritas(this.getPaises()[this.opc - 1].getNombre_pais(), this);
            } catch (ClassNotFoundException | SQLException ex) {
                Logger.getLogger(VALBUM.class.getName()).log(Level.SEVERE, null, ex);
            }
        }

    }//GEN-LAST:event_cmdAnteriorActionPerformed

